sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

	var requestType = newValue;
	g_form.clearOptions('u_what_needed');
	if(requestType === "hr"){
		g_form.addOption('u_what_needed', 'hr1', 'Human Resourecs 1');
		g_form.addOption('u_what_needed', 'hr2', 'Human Resourecs 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}else if(requestType === 'legal'){
		g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_what_needed', 'legal3', 'Legal 3');
	}else{
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'facilities3', 'Facilities 3');
	}

}
